#!/usr/bin/env python3

import globus_sdk

# sphinx extensions (minimally, we want autodoc and viewcode to build the site)
# plus, we have our own custom extension in the SDK to include
extensions = [
    "sphinx.ext.autodoc",
    "sphinx.ext.todo",
    "sphinx.ext.viewcode",
    "globus_sdk._sphinxext",
]

project = "globus-sdk-python"
copyright = "2016, Globus"
author = "Globus Team"
# The short X.Y version.
version = globus_sdk.__version__
# The full version, including alpha/beta/rc tags.
release = version

# The language for content autogenerated by Sphinx. Refer to documentation
# for a list of supported languages.
#
# This is also used if you do content translation via gettext catalogs.
# Usually you set "language" from the command line for these cases.
language = "en"

# List of patterns, relative to source directory, that match files and
# directories to ignore when looking for source files.
exclude_patterns = ["_build"]

# If true, `todo` and `todoList` produce output, else they produce nothing.
todo_include_todos = True


# HTML Theme Options
html_show_sourcelink = True
html_theme = "furo"
html_theme_options = {
    "light_css_variables": {
        "color-brand-primary": "#27518F",
    }
}
html_logo = "_static/logo.png"

# The name of the Pygments (syntax highlighting) style to use.
pygments_style = "friendly"

# Output file base name for HTML help builder.
htmlhelp_basename = "globus-sdk-pythondoc"
